MicroModal.init({openTrigger:"data-custom-open",disableScroll:false,awaitCloseAnimation:true});let modalWindow;function viewModal(url,param){const status=function(response){if(response.status!==200){return Promise.reject(new Error(response.statusText))}return Promise.resolve(response)};const json=function(response){return response.json()};let html=function(data){return data};return fetch(url,param).then(status).then(json).then(html).catch(function(error){console.log("error",error)})}document.querySelector(".button-shortcode-js").addEventListener("click",function(event){let url=event.target.dataset.windowUrl;this.setAttribute("disabled","disabled");viewModal(url,{method:"POST",headers:{"Content-Type":"application/json;charset=utf-8"}}).then(function(result){Promise.this.insertAdjacentHTML("afterend",result.html)})});